mapper.select:
SELECT 

FROM energy_devices a

WHERE a.id = #{id}

SELECT 

FROM energy_devices a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.deviceid = #{deviceId}


		AND a.type = #{type}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM energy_devices a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE energy_devices SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
